import React, { useState, useEffect, useRef } from 'react'
import "./chatContainer.css"
import Logo from "../../assets/img/logo-small.png";
import Logout from '../logout/Logout';
import ChatInput from '../chatInput/ChatInput';
import axios from "axios"
import { sendMessageRoute, getAllMessagesRoute } from '../../utils/APIRoutes';
import { v4 as uuidv4 } from "uuid"

const ChatContainer = ({ currentChat, currentUser, socket }) => {
    const [messages, setMessages] = useState([]);
    const [arrivalMessage, setArrivalMessage] = useState(null);
    const [writting, setWritting] = useState("");
    const scrollRef = useRef();
    var typing = false;
    var timeout = undefined;

    useEffect(async () => {
        if (currentUser) {
            const response = await axios.post(getAllMessagesRoute, {
                from: currentUser._id,
                to: currentChat._id
            })
            setMessages(response.data.projectedMessages);
        }
    }, [currentChat])
    useEffect(() => {
        if (socket.current) {
            socket.current.on("msg-recieve", (msg) => {
                setArrivalMessage({ fromSelf: false, message: msg, createdAt: Date.now() })
            })
            socket.current.on("get-writing", (data) => {
                setWritting(data)
                
            })
        }
    }, [])
    useEffect(() => {
        arrivalMessage && setMessages((prev) => [...prev, arrivalMessage])
    }, [arrivalMessage])
    useEffect(() => {
        scrollRef.current?.scrollIntoView({ behaviour: "smooth" })
    }, [messages])
    const handleSendMsg = async (msg) => {
        await axios.post(sendMessageRoute, {
            from: currentUser._id,
            to: currentChat._id,
            message: msg
        })
        socket.current.emit("send-msg", {
            from: currentUser._id,
            to: currentChat._id,
            message: msg
        })
        const msgs = [...messages];
        msgs.push({ fromSelf: true, message: msg });
        setMessages(msgs)
    }
    const handleKeyPress = async (e) => {
        if (e.which != 13) {
            typing = true;
            socket.current.emit("send-writing", {
                from: currentUser._id,
                to: currentChat._id,
                typing:true
            })
            clearTimeout(timeout);
            timeout=setTimeout(()=>{
                socket.current.emit("send-writing", {
                    from: currentUser._id,
                    to: currentChat._id,
                    typing:false
                })
            },1000)
        } else {
            clearTimeout(timeout)
            
        }
       
        
    }
    return (
        <div>
            {currentChat &&
                <div className='chatContainer__content'>
                    <div className="chat__header">
                        <div className="user__details">
                            <div className="avatar">
                                <img src={Logo} alt="" />
                            </div>
                            <div className="username">
                                <h3>{currentChat.username}</h3>
                            </div>
                            <div className="username">
                                <h3>{writting==true  ? "writing..":""}</h3>
                            </div>
                        </div>
                        <Logout />
                    </div>
                    <div className="chat__messages">
                        {
                            messages.map((message) => {
                                return (
                                    <div ref={scrollRef} key={uuidv4()}>
                                        <div className={`message ${message.fromSelf ? "sended" : "recieved"}`}>
                                            <div className="message__content">
                                                <p>
                                                    {message.message}
                                                </p>
                                            </div>
                                        </div>

                                    </div>
                                )
                            })}
                    </div>
                    <ChatInput handleSendMsg={handleSendMsg} handleKeyPress={handleKeyPress} />
                </div>
            }
        </div>
    )
}

export default ChatContainer
