import React, { useState, useEffect } from 'react'
import "./contact.css";
import Logo from "../../assets/img/logo-small.png";

const Contact = ({ contacts, currentUser,changeChat }) => {
  const [currentUserName, setCurrentUserName] = useState(undefined);
  const [currentSelected, setCurrentSelected] = useState(undefined);
  useEffect(() => {
    if (currentUser) {
      setCurrentUserName(currentUser.username);
    }
  }, [currentUser])
  const changeCurrentChat = (index, contact) => {
    setCurrentSelected(index)
    changeChat(contact)

  }
  return (
    <div className='contact__container'>
      <div className="brand">
        <h5>Chatt</h5>
      </div>
      <div className="contacts">
        {
          contacts.map((contact, index) => {
            return (
              <div className={` contact ${index === currentSelected ? "selected" : ""}`} onClick={()=>changeCurrentChat} key={index}>
                <div className="avatar">
                  <img src={Logo} alt="" />
                </div>
                <div className="username">
                  <h3>{contact.username}</h3>
                </div>
              </div>
            )
          })
        }
        {
          contacts.map((contact, index) => {
            return (
              <div className={` contact ${index === currentSelected ? "selected" : ""}`} key={index}>
                <div className="avatar">
                  <img src={Logo} alt="" />
                </div>
                <div className="username">
                  <h3>{contact.username}</h3>
                </div>
              </div>
            )
          })
        }
        {
          contacts.map((contact, index) => {
            return (
              <div className={` contact ${index === currentSelected ? "selected" : ""}`} key={index}>
                <div className="avatar">
                  <img src={Logo} alt="" />
                </div>
                <div className="username">
                  <h3>{contact.username}</h3>
                </div>
              </div>
            )
          })
        }
      </div>
      <div className="current__user">
        <div className="avatar">
          <img src={Logo} alt="" />
        </div>
        <div className="username">
          <h3>{currentUserName}</h3>
        </div>
      </div>
    </div>
  )
}

export default Contact
