import React, { useState, useEffect, useRef } from 'react'
import Logout from '../logout/Logout';
import ChatInput from '../chatInput/ChatInput';
import axios from "axios"
import { sendRoomMessageRoute, getAllRoomMessagesRoute } from '../../utils/APIRoutes';
import { v4 as uuidv4 } from "uuid"

const ChatRoomContainer = ({ currentRoom, currentUser, socket }) => {
  console.log(currentRoom)
  const [messages, setMessages] = useState([]);
  const [arrivalMessage, setArrivalMessage] = useState(null);
  const [writting, setWritting] = useState("");
  const scrollRef = useRef();
  var userTyping = false;
  var userTimingTimeout = undefined;

  useEffect(() => {
    if (currentRoom) {
      socket.current.emit("join-room", { name: currentUser.username, roomId: currentRoom, userId: currentUser._id })
    }
  },[])

  useEffect(async () => {
    // console.log(currentRoom)
    // if (currentUser) {
    //   const response = await axios.post(getAllRoomMessagesRoute, {
    //     roomId: currentRoom
    //   })
    //   setMessages(response.data.messages);
    //   console.log(messages)
    // }
  },[])
  // useEffect(() => {
  //   if (socket.current) {
  //     socket.current.on("msg-recieve", (msg) => {
  //       setArrivalMessage({ fromSelf: false, message: msg, createdAt: Date.now() })
  //     })
  //     socket.current.on("get-writing", (data) => {
  //       setWritting(data)
  //     })
  //   }
  // }, [])
  // useEffect(() => {
  //   arrivalMessage && setMessages((prev) => [...prev, arrivalMessage])
  // }, [arrivalMessage])
  // useEffect(() => {
  //   scrollRef.current?.scrollIntoView({ behaviour: "smooth" })
  // }, [messages])
  const handleSendMsg = async (msg) => {
    await axios.post(sendRoomMessageRoute, {
      name: currentUser.username,
      userId: currentUser._id,
      roomId: currentRoom,
      message: msg
    })
    socket.current.emit("send-msg-room", {
      name: currentUser.username,
      userId: currentUser._id,
      roomId: currentRoom,
      message: msg
    })
    const msgs = [...messages];
    msgs.push({
      name: currentUser.username,
      userId: currentUser._id,
      roomId: currentRoom,
      message: msg
    });
    setMessages(msgs)
    console.log(messages)
  }
  const handleKeyPress = async (e) => {
    // if (e.which != 13) {
    //   userTyping = true;
    //   socket.current.emit("send-writing", {
    //     from: currentUser._id,
    //     to: currentChat._id,
    //     userTyping: true
    //   })
    //   clearTimeout(userTimingTimeout);
    //   userTimingTimeout = setTimeout(() => {
    //     socket.current.emit("send-writing", {
    //       from: currentUser._id,
    //       to: currentChat._id,
    //       userTyping: false
    //     })
    //   }, 1000)
    // } else {
    //   clearTimeout(userTimingTimeout)
    // }
  }
  return (
    <div>

      <div className='chatContainer__content'>
        <div className="chat__header">
          <div className="user__details">
            <div className="username">
              <h3>{currentRoom}</h3>
            </div>
            <div className="username">
              <h3>{writting == true ? "writing.." : ""}</h3>
            </div>
          </div>
          <Logout />
        </div>
        <div className="chat__messages">
          {
           messages.map((message,index)=>{
             <div>{message}</div>
           })
          }
        </div>
        <ChatInput handleSendMsg={handleSendMsg} handleKeyPress={handleKeyPress} />
      </div>

    </div>
  )
}

export default ChatRoomContainer
