import React from 'react'
import { useState } from 'react';
import { Link } from 'react-router-dom';

const Register = () => {
    const [values,setValues]=useState({
        username:   "",
        email:"",
        password:"",
        confirmPassword:""
    })
    const handleSubmit=(event)=>{
        console.log(7346382)
        event.preventDefault();
        handleValidation();
    }
    const handleValidation=()=>{
        const {password,confirmPassword,username,email}=values;
        console.log(password)
        console.log(confirmPassword)
        if(password !==confirmPassword){
           console.log("password and confirm password should be same");
           return false;
        }else if(username.length <3){
            console.log("username 3 length")
        }else if(password.length < 8){
            console.log("passeord length 8")
        }
    }
    const handleChange = (event)=>{
        console.log(event)
        setValues({...values,[event.target.name]:event.target.value})

    }
  return (
    <form onSubmit={(event)=>handleSubmit(event)}>
        <input type="text" name="username" id="" placeholder='username' />
        <input type="text" name="email" id="" placeholder='email' />
        <input type="text" name="password" id="" placeholder='pass1' onChange={(e)=>handleChange(e)}/>
        <input type="text" name="confirmPassword" id="" placeholder='pass2' onChange={(e)=>handleChange(e)}/>
        <button type='submit'>Create User</button>
        <span>alreaady login <Link to="/login"></Link> </span>

    </form>
  )
}

export default Register
