import React, { useState, useEffect, useRef } from 'react'
import axios from 'axios';
import "./chat.css";
import { useNavigate } from 'react-router-dom';
import { allUsersRoute, host, getChatRoom } from '../../utils/APIRoutes';
import Contact from '../../components/contact/Contact';
import Welcome from '../../components/welcome/Welcome';
import ChatContainer from '../../components/chatContainer/ChatContainer';
import { io } from "socket.io-client"

const Chat = () => {
  const socket = useRef();
  const navigate = useNavigate();
  const [contacts, setContacts] = useState([]);
  const [roomList, setRoomList] = useState([]);

  const [currentUser, setCurrentUser] = useState(undefined);
  const [currentChat, setCurrentChat] = useState(undefined);
  const [isLoaded, setIsLoaded] = useState(false);
  const [onlineUser, setOnline] = useState([]);

  useEffect(async () => {
    if (!localStorage.getItem("chat-app-user")) {
      navigate("/login")
    } else {
      setCurrentUser(await JSON.parse(localStorage.getItem("chat-app-user")))
      setIsLoaded(true)
    }
  }, [])
  useEffect(() => {
    if (currentUser) {
      socket.current = io(host);
      socket.current.emit("add-user", currentUser._id);
      socket.current.on("get-users", (users) => {
        setOnline(users)
      })
    }
  }, [currentUser])

  useEffect(async () => {
    if (currentUser) {
      const data = await axios.get(`${allUsersRoute}/${currentUser._id}`);
      setContacts(data.data)
    }
  }, [currentUser])
  useEffect(async ()=>{
    const data1 = await axios.get(`${getChatRoom}`);
    setRoomList(data1.data)
  })

  const handleChatChange = (chat) => {
    setCurrentChat(chat);
  }
  return (
    <div className='chat'>
      <div className="container">
        <Contact contacts={contacts} currentUser={currentUser} onlineUser={onlineUser} changeChat={handleChatChange} roomList={roomList} />
        {
          isLoaded && currentChat === undefined ?
            (<Welcome currentUser={currentUser} />) :
            (<ChatContainer currentChat={currentChat} currentUser={currentUser} socket={socket} />)
        }
      </div>
    </div>
  )
}
export default Chat
