import React, { useEffect } from 'react'
import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from "axios";
import { loginRoute } from '../../utils/APIRoutes';
import "./login.css";

const Login = () => {
  const navigate = useNavigate();
  const [values, setValues] = useState({
    username: "",
    password: "",
  })
  useEffect(() => {
    if (localStorage.getItem("chat-app-user")) {
      navigate('/')
    }
  }, [])
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (handleValidation()) {
      console.log("gitti")
      const { password, username, } = values;
      const { data } = await axios.post(loginRoute, {
        username, password
      })
      if (data.status == false) {
        console.log(data.msg)
      }
      if (data.status == true) {
        localStorage.setItem("chat-app-user", JSON.stringify(data.user));
        console.log("başarılı")
        navigate("/");
      }


    }
  }
  const handleValidation = () => {
    const { password, username } = values;
    if (password === "") {
      console.log("password and confirm password should be same");
      return false;
    } else if (username.length === "") {
      return false;
    }
    return true;
  }
  const handleChange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value })

  }
  return (
    <div className='login__container'>

      <form onSubmit={(event) => handleSubmit(event)} className="login__form">
        <div className="title">
          <h3>Chatt App</h3>
        </div>
        <input type="text" name="username" id="" placeholder='username' onChange={(e) => handleChange(e)} />

        <input type="text" name="password" id="" placeholder='password' onChange={(e) => handleChange(e)} />

        <button type='submit'>Log In</button>
        <span>
          Don't have an account ? <Link to="/register">Create One.</Link>
        </span>

      </form>

    </div>
  )
}

export default Login
