import React, { useState, useEffect } from 'react'
import "./contact.css";
import Logo from "../../assets/img/logo-small.png";

const Contact = ({ contacts, currentUser, onlineUser, changeChat, roomList, rommChange }) => {
  const [currentUserName, setCurrentUserName] = useState(undefined);
  const [currentSelected, setCurrentSelected] = useState(undefined);
  const [roomChatSelected, setroomChatSelected] = useState(undefined);
  const [userList, setUserLİst] = useState([]);

  useEffect(() => {
    var array=[]
    var obj={}
    for (var i = 0; i < contacts.length; i++) {
      if (onlineUser.length > 0) {
        for (var j = 0; j < onlineUser.length; j++) {
          var id=contacts[i]._id
          console.log(contacts[i]._id)
          console.log(onlineUser[j].userId)
          if (!obj[id]) {
            obj[id] = { name:contacts[i].username,connect:false }
        }
          if(contacts[i]._id==onlineUser[j].userId){
            obj[id].connect=true
            console.log("bağlı")
          }
          console.log(onlineUser[j]);
        }
      }
      array.push(obj)
    }
    console.log(array)
  }, [onlineUser])
  useEffect(() => {
    if (currentUser) {
      setCurrentUserName(currentUser.username);
    }
  }, [currentUser])
  const changeCurrentChat = (index, contact) => {
    setCurrentSelected(index)
    changeChat(contact)
  }
  const changeRoomChat = (index, room) => {
    setroomChatSelected(room)
    rommChange(room)
  }
  return (
    <div className='contact__container'>
      <div className="brand">
        <h5>Chat Room</h5>
      </div>
      <div className="contacts">
        {
          roomList.map((room, index) => {
            return (
              <div className={`contact__chatroom contact ${index === roomChatSelected ? "selected" : ""}`} onClick={() => changeRoomChat(index, room)} key={index}>
                {room}
              </div>
            )
          })
        }
      </div>

      <div className="brand">
        <h5>Users List</h5>
      </div>
      <div className="contacts">
        {
          contacts.map((contact, index) => {
            return (
              <div className={` contact ${index === currentSelected ? "selected" : ""}`} onClick={() => changeCurrentChat(index, contact)} key={index}>
                <div className="avatar">
                  <img src={Logo} alt="" />
                </div>
                <div className="username">
                  <h3>{contact.username}</h3>
                </div>

              </div>
            )
          })
        }
      </div>
      <div className="current__user">
        <div className="avatar">
          <img src={Logo} alt="" />
        </div>
        <div className="username">
          <h3>{currentUserName}</h3>
        </div>
      </div>
    </div>
  )
}

export default Contact
